import java.util.*;import java.lang.*;import java.io.*;public class Codechef{	public static void main (String[] args) throws java.lang.Exception	{        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        while(t-->0){            int n=sc.nextInt();            int arr[]=new int[n];        int f=0;        for(int i=0;i<n;i++)        {            arr[i]=sc.nextInt();        }         for(int i=1;i<n;i++)        {           if(arr[i]<arr[i-1]){           f=1;break;}        }        if(f==1)       System.out.println(1);        else         System.out.println(n);	        }        }}